CREATE TABLE ais_data_harmonized(mmsi int, STATUS int, speed int, longitude float, latitude float, course int, heading int, datetime timestamp, Ship_ID int, provider varchar(20));

CREATE TABLE weather_data_harmonized(provider varchar(20), datetime timestamp, lat double precision, lon double precision, main_temp double precision, min_temp double precision, max_temp double precision, main_pressure double precision, humidity double precision, main_wind_speed double precision, main_wind_direction double precision, sea_level double precision, main_visibility double precision, main_wind_gust double precision, main_clouds double precision, country varchar(20), sunrise_time timestamp, sunset_time timestamp, main_precipitation double precision, max_precipitation double precision, min_precipitation double precision, probability_of_precipitation double precision, probability_of_thunder double precision);